mapper.insert:
insert into CG_CODE (
	CODE_IDX
	, CODE_FIRST
	, CODE_TWO
	, CODE_CODENAME
	, CODE_ISUSE
) values (
	#{code_idx}
	, 
	, '00000'
	, #{code_codeName}
	, 'Y'
)

insert into CG_CODE (
	CODE_IDX
	, CODE_FIRST
	, CODE_TWO
	, CODE_CODENAME
	, CODE_ISUSE
) values (
	#{code_idx}
	, #{code_first}
	, 
	, #{code_codeName}
	, 'Y'
)

mapper.update:
update CG_CODE set 
	CODE_CODENAME=#{code_codeName}
	, CODE_ISUSE=#{code_isuse}
where
	CODE_IDX=#{code_idx}
	and CODE_FIRST=#{code_first}
	and CODE_TWO='00000'

update CG_CODE set 
	CODE_CODENAME=#{code_codeName}
	, CODE_ISUSE=#{code_isuse}
where
	CODE_IDX=#{code_idx}
	and CODE_FIRST=#{code_first}
	and CODE_TWO=#{code_two}
