mapper.select:
select LD_SEQ , LD_ID , LD_PWD , LD_GROUPNM , case LD_GROUPGUBN when '0' then '학원' when '1' then '교습소' when '2' then '공부방' when '3' then '교육단체' when '4' then '일반리더' end as LD_GROUPGUBN , LD_NAME , LD_AUTHCODE , LD_AUTHCHK , LD_POST , LD_ADDRESS , LD_ADDRESSDETAIL , LD_TEL , LD_FAX , LD_HP , LD_SNSCHK , LD_EMAIL , LD_EMAILSENDCHK , LD_GROUPINFO , LD_REGION , LD_ORGSALERATE , LD_ORGMILEGERATE , LD_BANKNM , LD_BANKNUM , LD_BANKMEMNM , LD_BANKBOOKIMG , LD_LOGOIMG , LD_COMPNUMIMG , DATE_FORMAT( REGDATE,'%Y-%m-%d' ) as REGDATE , LD_COMGUBN from ( select LD_SEQ , LD_ID , LD_PWD , LD_GROUPNM , LD_GROUPGUBN , LD_NAME , LD_AUTHCODE , LD_AUTHCHK , LD_POST , LD_ADDRESS , LD_ADDRESSDETAIL , LD_TEL , LD_FAX , LD_HP , LD_SNSCHK , LD_EMAIL , LD_EMAILSENDCHK , LD_GROUPINFO , LD_REGION , LD_ORGSALERATE , LD_ORGMILEGERATE , LD_BANKNM , LD_BANKNUM , LD_BANKMEMNM , LD_BANKBOOKIMG , LD_LOGOIMG , LD_COMPNUMIMG , REGDATE , case LD_COMGUBN when '0' then '승인대기' when '1' then '승인완료' end as LD_COMGUBN , @rnum := @rnum + 1 as rn from (SELECT @rnum := 0) r, CG_LEADER where LD_GROUPGUBN in ('0', '1', '2', '3')
and LD_GROUPGUBN = #{ld_groupgubn}
and LD_COMGUBN = #{ld_comgubn}
order by REGDATE desc ) as list where list.rn between #{start} and #{end}

mapper.update:
update
	LD_PWD = #{ld_pwd}
	, LD_GROUPNM = #{ld_groupnm}
	, LD_NAME = #{ld_name}
	, LD_AUTHCODE = #{ld_authcode}
	, LD_AUTHCHK = #{ld_authchk}
	, LD_POST = #{ld_post}
	, LD_ADDRESS = #{ld_address}
	, LD_ADDRESSDETAIL = #{ld_addressdetail}
	, LD_TEL = #{ld_tel}
	, LD_FAX = #{ld_fax}
	, LD_HP = #{ld_hp}
	, LD_SNSCHK = #{ld_snschk}
	, LD_EMAIL = #{ld_email}
	, LD_EMAILSENDCHK = #{ld_emailsendchk}
	, LD_GROUPINFO = #{ld_groupinfo}
	, LD_REGION = #{ld_region}
	, LD_ORGSALERATE = #{ld_orgsalerate}
	, LD_ORGMILEGERATE = #{ld_orgmilegerate}
	, LD_BANKNM = #{ld_banknm}
	, LD_BANKNUM = #{ld_banknum}
	, LD_BANKMEMNM = #{ld_bankmemnm}
	, LD_BANKBOOKIMG = #{ld_bankbookimg}
	, LD_LOGOIMG = #{ld_logoimg}
	, LD_COMPNUMIMG = #{ld_compnumimg}
	, LD_COMGUBN = #{ld_comgubn}
where
	LD_SEQ=#{ld_seq}
