mapper.insert:
insert into CG_SUPPLIER(
	SP_SEQ
	, SP_ID
	, SP_PWD
	, SP_BUSINM
	, SP_NAME
	, SP_USERATING
	, SP_GRADE
	, SP_REGDATE
) values (
	makeSupplyId()
	, #{sp_id}
	, #{sp_pwd}
	, #{sp_businm}
	, #{sp_name}
	, #{sp_userating}
	, '0'
	, now()
)

