mapper.select:
select SP_SEQ , SP_ID , SP_PWD , SP_BUSINM , SP_NAME , SP_USERATING , SP_POST , SP_ADDRESS , SP_DETAILADDRESS , SP_TEL , SP_FAX , SP_HP , SP_SNSCHK , SP_EMAIL , SP_EMAILSENDCHK , SP_POINT , SP_OUTPOINT , SP_INPOINT , SP_COMPINTRO , SP_ITEMS , SP_MANAGER , SP_ORGRATE , SP_FEESCHK , SP_DVRTYPE , SP_DVRTYPEETC , SP_DVRCOMP , SP_DVRSTANDARD , SP_DVRPRICE , SP_DVRRETURN , SP_DVRRETURNETC , SP_DVRRETURNCOMP , SP_BANKNM , SP_BANKNUM , SP_BANKDEPNM , SP_COMPNUMIMG , SP_BANKBOOKIMG , SP_LOGOIMG , SP_GRADE , SP_GRADECHK , SP_REGDATE from ( select SP_SEQ , SP_ID , SP_PWD , SP_BUSINM , SP_NAME , SP_USERATING , SP_POST , SP_ADDRESS , SP_DETAILADDRESS , SP_TEL , SP_FAX , SP_HP , SP_SNSCHK , SP_EMAIL , SP_EMAILSENDCHK , SP_POINT , SP_OUTPOINT , SP_INPOINT , SP_COMPINTRO , SP_ITEMS , SP_MANAGER , SP_ORGRATE , SP_FEESCHK , SP_DVRTYPE , SP_DVRTYPEETC , SP_DVRCOMP , SP_DVRSTANDARD , SP_DVRPRICE , SP_DVRRETURN , SP_DVRRETURNETC , SP_DVRRETURNCOMP , SP_BANKNM , SP_BANKNUM , SP_BANKDEPNM , SP_COMPNUMIMG , SP_BANKBOOKIMG , SP_LOGOIMG , SP_GRADE , SP_GRADECHK , SP_REGDATE , @rnum := @rnum + 1 as rn from (SELECT @rnum := 0) r, CG_SUPPLIER where 1=1
and SP_BUSINM like CONCAT('%', #{search_businm}, '%')
) as list where list.rn between #{start} and #{end}

mapper.update:
update 
	CG_SUPPLIER
