	SP_BUSINM = #{sp_businm}
	, SP_NAME = #{sp_name}
	, SP_USERATING = #{sp_userating}
	, SP_POST = #{sp_post}
	, SP_ADDRESS = #{sp_address}
	, SP_DETAILADDRESS = #{sp_detailAddress}
	, SP_TEL = #{sp_tel}
	, SP_FAX = #{sp_fax}
