/* 숫자만 입력 */
function digit_check(evt){
    var code = evt.which?evt.which:event.keyCode;
    if(code < 48 || code > 57){
        return false;
    }
}

/* 소숫점+입력글자제한 */
function lfn_keychk(obj){
 var val = obj.value;
 if (val)
 {
  var re = /[^0-9|.]/gi;
  obj.value = val.replace(re, '');
  var split = val.split(".");
  if(split.length > 2) {  
   obj.value = val.substr(0,val.length-1);
  }
  if(split[0] > 99){   
   if(split[0].length > 2) {
    obj.value = val.substr(0,val.length-1);
   }
  }
  if(split[1] != null){   
   if(split[1].length > 3) {
    obj.value = val.substr(0,val.length-1);
   }
  }
 }
}

function ChkByte(objname,maxlength) { 
 var objstr = objname.value;
 var objstrlen = objstr.length; 

 
 var maxlen = maxlength; 
 var i = 0; 
 var bytesize = 0; 
 var strlen = 0; 
 var onechar = "";  
 var objstr2 = ""; 

 
 for(i=0; i< objstrlen; i++) { 
 
  onechar = objstr.charAt(i); 
  
  if (escape(onechar).length > 4) { 
   bytesize += 2; 
  } else {  
   bytesize++;     
  } 
  
  if(bytesize <= maxlen)  {   
   strlen = i + 1;   
  }
 }

 if(bytesize > maxlen) { 
  alert( "입력할 수 있는 글자 수를 초과했습니다. \n초과된 내용은 자동으로 삭제 됩니다."); 
  objstr2 = objstr.substr(0, strlen); 
  objname.value = objstr2; 
 } 
 objname.focus(); 
} 

