/* 필수입력 메세지(빠진부분 있어요) */
	function submitChk() {
		var f = document.Frm;
		var objItem;

		if (f.chkID.value=="") {
			alert("아이디를 입력해 주세요.");
			f.chkID.focus();
			return false;
		}

		if (f.id.value != f.chkID.value){
			alert("아이디 중복확인을 해주세요.");
			f.chkID.focus();
			return false;
		}

		if (f.pass.value=="") {
			alert("비밀번호를 입력해 주세요.");
			f.pass.focus();
			return false;
		}
		if (f.id.value == f.pass.value){
			alert("비밀번호는 아이디와 동일하게 할 수 없습니다.");
			f.pass.focus();
			return false;
		}

		if (f.rePass.value==""){
			alert("비밀번호 확인을 입력해 주세요.");
			f.rePass.focus();
			return false;
		}

		if (f.newpass.value != f.rePass.value){
			alert("비밀번호를 다시 확인해 주세요.");
			f.rePass.focus();
			return false;
		}

		if (f.name.value==""){
			alert("업체명을 입력해 주세요.");
			f.name.focus();
			return false;
		}
		
		if (f.name01.value==""){
			alert("대표자명을 입력해 주세요.");
			f.name.focus();
			return false;
		} 
		
		for (i=1; i<=3; i++) {
			objItem = eval("f.mobile"+i);
			if (checkEmpty(objItem)) {
				alert("휴대폰번호를 입력해 주세요.");
				objItem.focus();
				return false;
			}
		}
		
		if (f.emailID.value==""){
			alert("이메일 주소를 입력해 주세요.");
			f.emailID.focus();
			return false;
		}

		if (!checkEmail(f.emailID.value+"@"+f.emailDomain.value)) {
			alert("형식에 맞지 않는 이메일 주소입니다.\n\n이메일 주소를 정확하게 입력해 주세요.");
			if (f.emailDomain.readOnly) f.emailID.focus();
			else f.emailDomain.focus();
			return false;
		}
		

		if (f.post1.value==""){
			alert("주소를 입력해 주세요.");
			return false;
		}

		if (f.addrDetail.value==""){
			alert("주소를 입력해 주세요.");
			f.addrDetail.focus();
			return false;
		}

	}
	
	/* 레이어 팝업 */
	function dEI(elementID){
	return document.getElementById(elementID);
}


function openLayer(IdName, tpos, lpos){
	var pop = dEI(IdName);
	pop.style.top = tpos + "px";
	pop.style.left = lpos + "px";
	pop.style.display = "block";

	var wrap = dEI("layerPop");
	var reservation = document.createElement("div");
	wrap.appendChild(reservation);
}

function closeLayer( IdName ){
	var pop = dEI(IdName);
	pop.style.display = "none";
	var momEl = parent.dEI("layerPop");
	momEl.removeChild(clearEl);
}

